mapper.select:
select * from computer_software t where 1=1 and id = #{id}


    and UNIT_NAME = #{UNIT_NAME}


    and REGISTER_NUMBER = #{REGISTER_NUMBER}


    and SOFTWARE_NAME = #{SOFTWARE_NAME}


    and COPYRIGHT_OWNER = #{COPYRIGHT_OWNER}


    and VERSION_NUMBER = #{VERSION_NUMBER}


    and DATE_FORMAT(t.`RECORD_DATE`, '%Y-%m-%d') >= DATE_FORMAT(#{recordDateStart}, '%Y-%m-%d')


    and DATE_FORMAT(t.`RECORD_DATE`, '%Y-%m-%d') <= DATE_FORMAT(#{recordDateEnd}, '%Y-%m-%d')


    and DEVELOP_FINISH_DATE = #{DEVELOP_FINISH_DATE}


    and SOFTWARE_INTRO = #{SOFTWARE_INTRO}


    and ENTRY_PEOPLE = #{ENTRY_PEOPLE}


    and ENTRY_TIME = #{ENTRY_TIME}


    and NOTES = #{NOTES}


    and ACCESSORY_UPLOAD = #{ACCESSORY_UPLOAD}


    update_date = #{updateDate},


    updator = #{updator,jdbcType=VARCHAR},


    deleted = #{deleted,jdbcType=VARCHAR}

and deleted != '1'
