mapper.insert:
    insert into zjk_patent 
     (
     
      id,expert_id, source_type, del_status, 
      patent_name, patent_type, `describer`,
      get_patent_time, create_time, out_system_id,flag,secret_level,knowledge_scope,knowledge_person,create_user
      )
    values (
	      #{id,jdbcType=VARCHAR},
	      #{expertId,jdbcType=VARCHAR}, #{sourceType,jdbcType=VARCHAR}, #{delStatus,jdbcType=VARCHAR}, 
	      #{patentName,jdbcType=VARCHAR}, #{patentType,jdbcType=VARCHAR}, #{describe,jdbcType=VARCHAR},
	      #{getPatentTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}, #{outSystemId,jdbcType=VARCHAR},
	      #{flag,jdbcType=VARCHAR}, #{secretLevel,jdbcType=VARCHAR}
       ,#{knowledgeScope,jdbcType=VARCHAR}
      ,#{knowledgePerson,jdbcType=VARCHAR}
      ,#{createUser,jdbcType=VARCHAR}
      )

mapper.select:
select * from ()  t     

 ${orderBySql}
 
   ORDER BY  create_time desc
 

 limit #{start},#{pageSize}

select  * from view_zjk_patent where 1=1 and expert_id = #{expertId}
and del_status != '1'

  and secret_level = #{secretLevel}

and (secret_level = '0' or (
secret_level <= #{userSecretLevel}

  and FIND_IN_SET(#{userName}, knowledge_scope) < 0

))
