mapper.select:
select

  distinct


from patent_info




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select   from view_patent_info where 1=1

  and unit_name = #{unitName}


  and application_type = #{applicationType}


  and patent_type = #{patentType}


  and application_number = #{applicationNumber}


  and patent_name = #{patentName}


  and applicant = #{applicant}


  and inventor = #{inventor}


  and applicationDate >= #{applicationDateStart}


  and applicationDate <= #{applicationDateEnd}

and deleted != '1'
