mapper.select:
select  * from view_kypt_treatise where 1=1 and platform_id = #{platformId}
and deleted != '1'


    and secret_level = #{secretLevel}

and (secret_level = '0' or (
secret_level <= #{userSecretLevel}

    and FIND_IN_SET(#{userName}, knowledge_scope) < 0

))

select  count(*) from kypt_treatise where 1=1 and platform_id = #{platformId}
and deleted != '1'


    and secret_level = #{secretLevel}

and (secret_level = '0' or (
secret_level <= #{userSecretLevel}

    and FIND_IN_SET(#{userName}, knowledge_scope) < 0

))

mapper.insert:
insert into kypt_treatise( id, platform_id, base_id, thesis_title, thesis_level, journal_title, thesis_author, thesis_year, deleted, create_user, create_date, update_date, update_user, create_unit_id, create_unit_name, secret_level, knowledge_scope, knowledge_person
) values( #{id}, #{platformId}, #{baseId}, #{thesisTitle}, #{thesisLevel}, #{journalTitle}, #{thesisAuthor}, #{thesisYear}, #{deleted}, #{creator}, #{createDate}, #{updateDate}, #{updator}, #{createUnitId}, #{createUnitName}, #{secretLevel}, #{knowledgeScope}, #{knowledgePerson}
)

insert into kypt_treatise( id,platform_id, base_id,thesis_title, thesis_level,journal_title, thesis_author, thesis_year,deleted, create_user,create_date, update_date,update_user, create_unit_id, create_unit_name, secret_level ) values
(#{kt.id},#{kt.platformId}, #{kt.baseId},#{kt.thesisTitle}, #{kt.thesisLevel}, #{kt.journalTitle}, #{kt.thesisAuthor}, #{kt.thesisYear}, ifnull(#{kt.deleted},'0'), #{kt.creator}, #{kt.createDate}, #{kt.updateDate}, #{kt.updator}, #{kt.createUnitId}, #{kt.createUnitName}, #{kt.secretLevel}
)
