mapper.select:
select

  distinct


from trademark_info




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select   from view_trademark_info where 1=1

  and unit_name = #{unitName}


  and trademark_name = #{trademarkName}


  and law_status = #{lawStatus}


  and applicant = #{applicant}


  and register_date >= #{registerDateStart}


  and register_date <= #{registerDateEnd}

and deleted != '1'
