mapper.select:
select

    distinct


from zjk_data




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
age,
expert_name,
sex,
birth_date,
political_face,
place_origin,
nation,
college,
education,
unit_belongs,
professional_and_time,
technical_positiion,
administrative_duties,
mobile,
email,
user_desc,
awards_title,
brief_desc,
data_id
from zjk_data
where 1=1

    and age = #{age,jdbcType=VARCHAR}


    and expert_name = #{expertName,jdbcType=VARCHAR}


    and sex = #{sex,jdbcType=VARCHAR}


    and birth_date = #{birthDate,jdbcType=VARCHAR}


    and political_face = #{politicalFace,jdbcType=VARCHAR}


    and place_origin = #{placeOrigin,jdbcType=VARCHAR}


    and nation = #{nation,jdbcType=VARCHAR}


    and college = #{college,jdbcType=VARCHAR}


    and education = #{education,jdbcType=VARCHAR}


    and unit_belongs = #{unitBelongs,jdbcType=VARCHAR}


    and professional_and_time = #{professionalAndTime,jdbcType=VARCHAR}


    and technical_positiion = #{technicalPositiion,jdbcType=VARCHAR}


    and administrative_duties = #{administrativeDuties,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}


    and user_desc = #{userDesc,jdbcType=VARCHAR}


    and awards_title = #{awardsTitle,jdbcType=VARCHAR}


    and brief_desc = #{briefDesc,jdbcType=VARCHAR}


    and data_id = #{dataId,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
