mapper.select:
select

    distinct


from zjk_train




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
data_id,
sys_flag,
data_order,
del_flag,
create_date,
create_user,
create_user_disp,
modify_date,
modify_user,
modify_user_disp,
form_code,
data_index,
status,
audit_status,
company_code,
update_user,
update_date,
project_id,
project_name,
zjk_id,
zjk_name,
train_theme,
train_date,
train_type,
train_user,
train_desc,
do_desc,
xm_steps,
xm_id,
xm_name,
bak1,
bak2,
bak3,
bak4,
bak5
from zjk_train
where 1=1

    and data_id = #{dataId,jdbcType=VARCHAR}


    and sys_flag = #{sysFlag,jdbcType=VARCHAR}


    and data_order = #{dataOrder,jdbcType=VARCHAR}


    and del_flag = #{delFlag,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=VARCHAR}


    and create_user = #{createUser,jdbcType=VARCHAR}


    and create_user_disp = #{createUserDisp,jdbcType=VARCHAR}


    and modify_date = #{modifyDate,jdbcType=VARCHAR}


    and modify_user = #{modifyUser,jdbcType=VARCHAR}


    and modify_user_disp = #{modifyUserDisp,jdbcType=VARCHAR}


    and form_code = #{formCode,jdbcType=VARCHAR}


    and data_index = #{dataIndex,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and audit_status = #{auditStatus,jdbcType=VARCHAR}


    and company_code = #{companyCode,jdbcType=VARCHAR}


    and update_user = #{updateUser,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=VARCHAR}


    and project_id = #{projectId,jdbcType=VARCHAR}


    and project_name = #{projectName,jdbcType=VARCHAR}


    and zjk_id = #{zjkId,jdbcType=VARCHAR}


    and zjk_name = #{zjkName,jdbcType=VARCHAR}


    and train_theme = #{trainTheme,jdbcType=VARCHAR}


    and train_date = #{trainDate,jdbcType=VARCHAR}


    and train_type = #{trainType,jdbcType=VARCHAR}


    and train_user = #{trainUser,jdbcType=VARCHAR}


    and train_desc = #{trainDesc,jdbcType=VARCHAR}


    and do_desc = #{doDesc,jdbcType=VARCHAR}


    and xm_steps = #{xmSteps,jdbcType=VARCHAR}


    and xm_id = #{xmId,jdbcType=VARCHAR}


    and xm_name = #{xmName,jdbcType=VARCHAR}


    and bak1 = #{bak1,jdbcType=VARCHAR}


    and bak2 = #{bak2,jdbcType=VARCHAR}


    and bak3 = #{bak3,jdbcType=VARCHAR}


    and bak4 = #{bak4,jdbcType=VARCHAR}


    and bak5 = #{bak5,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
