mapper.select:
select

    distinct


from report_config

    


    order by ${orderByClause}


    
        limit ${offset}, ${limit}
    
    
        limit ${limit}

        select
        id,
        name,
        sf_y,
        pic_type,
        model_name,
        model_index,
        bak1,
        bak2,
        bak3,
        bak4,
        bak5,
        bak6,
        create_user,
        create_user_id,
        create_date,
        status,
        update_date,
        update_user,
        remarks,
        audit_status
        from report_config
        where 1=1
        
            and id = #{id,jdbcType=VARCHAR}
        
        
            and name = #{name,jdbcType=VARCHAR}
        
        
            and sf_y = #{sfY,jdbcType=VARCHAR}
        
        
            and pic_type = #{picType,jdbcType=VARCHAR}
        
        
            and model_name = #{modelName,jdbcType=VARCHAR}
        
        
            and model_index = #{modelIndex,jdbcType=VARCHAR}
        
        
            and bak1 = #{bak1,jdbcType=VARCHAR}
        
        
            and bak2 = #{bak2,jdbcType=VARCHAR}
        
        
            and bak3 = #{bak3,jdbcType=VARCHAR}
        
        
            and bak4 = #{bak4,jdbcType=VARCHAR}
        
        
            and bak5 = #{bak5,jdbcType=VARCHAR}
        
        
            and bak6 = #{bak6,jdbcType=VARCHAR}
        
        
            and create_user = #{createUser,jdbcType=VARCHAR}
        
        
            and create_user_id = #{createUserId,jdbcType=VARCHAR}
        
        
            and create_date = #{createDate,jdbcType=VARCHAR}
        
        
            and status = #{status,jdbcType=VARCHAR}
        
        
            and update_date = #{updateDate,jdbcType=VARCHAR}
        
        
            and update_user = #{updateUser,jdbcType=VARCHAR}
        
        
            and remarks = #{remarks,jdbcType=VARCHAR}
        
        
            and audit_status = #{auditStatus,jdbcType=VARCHAR}
        

        
            order by ${iSortCol} ${sSortDir_0}
