mapper.select:
select

    distinct


from index_out_appraisal




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
data_id,
cgid,
nd,
cgmc,
sqjddw,
sqdjh,
startTime,
endTime,
cglx,
zy,
jdh,
issczs,
sbzt,
jdjg,
create_date,
create_user_id,
update_date,
update_user_id,
status,
remarks,
hth,
xmmc,
rwly,
define1,
define2,
define3,
define4,
define5,
define6,
tfm_type_id,
type_code,
type_name,
parent_id,
parent_code,
is_parent,
type_index
from index_out_appraisal
where 1=1

    and data_id = #{dataId,jdbcType=VARCHAR}


    and cgid = #{cgid,jdbcType=VARCHAR}


    and nd = #{nd,jdbcType=VARCHAR}


    and cgmc = #{cgmc,jdbcType=VARCHAR}


    and sqjddw = #{sqjddw,jdbcType=VARCHAR}


    and sqdjh = #{sqdjh,jdbcType=VARCHAR}


    and startTime = #{starttime,jdbcType=VARCHAR}


    and endTime = #{endtime,jdbcType=VARCHAR}


    and cglx = #{cglx,jdbcType=VARCHAR}


    and zy = #{zy,jdbcType=VARCHAR}


    and jdh = #{jdh,jdbcType=VARCHAR}


    and issczs = #{issczs,jdbcType=VARCHAR}


    and sbzt = #{sbzt,jdbcType=VARCHAR}


    and jdjg = #{jdjg,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=VARCHAR}


    and create_user_id = #{createUserId,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=VARCHAR}


    and update_user_id = #{updateUserId,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and remarks = #{remarks,jdbcType=VARCHAR}


    and hth = #{hth,jdbcType=VARCHAR}


    and xmmc = #{xmmc,jdbcType=VARCHAR}


    and rwly = #{rwly,jdbcType=VARCHAR}


    and define1 = #{define1,jdbcType=VARCHAR}


    and define2 = #{define2,jdbcType=VARCHAR}


    and define3 = #{define3,jdbcType=VARCHAR}


    and define4 = #{define4,jdbcType=VARCHAR}


    and define5 = #{define5,jdbcType=VARCHAR}


    and define6 = #{define6,jdbcType=VARCHAR}


    and tfm_type_id = #{tfmTypeId,jdbcType=VARCHAR}


    and type_code = #{typeCode,jdbcType=VARCHAR}


    and type_name = #{typeName,jdbcType=VARCHAR}


    and parent_id = #{parentId,jdbcType=VARCHAR}


    and parent_code = #{parentCode,jdbcType=VARCHAR}


    and is_parent = #{isParent,jdbcType=VARCHAR}


    and type_index = #{typeIndex,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
