mapper.select:
select

    distinct


from sys_file_config

    


    order by ${orderByClause}


    
        limit ${offset}, ${limit}
    
    
        limit ${limit}

        select
        id,
        menu_id,
        position,
        file_size,
        file_count,
        file_height,
        file_width,
        file_key,
        create_time,
        create_user,
        state,
        file_type,
        file_theme,
        bz,
        img_type,
        img_desc,
        img_quality,
        bak1,
        bak2,
        bak3
        from sys_file_config
        where 1=1
        
            and id = #{id,jdbcType=VARCHAR}
        
        
            and menu_id = #{menuId,jdbcType=VARCHAR}
        
        
            and position = #{position,jdbcType=VARCHAR}
        
        
            and file_size = #{fileSize,jdbcType=VARCHAR}
        
        
            and file_count = #{fileCount,jdbcType=VARCHAR}
        
        
            and file_height = #{fileHeight,jdbcType=VARCHAR}
        
        
            and file_width = #{fileWidth,jdbcType=VARCHAR}
        
        
            and file_key = #{fileKey,jdbcType=VARCHAR}
        
        
            and create_time = #{createTime,jdbcType=VARCHAR}
        
        
            and create_user = #{createUser,jdbcType=VARCHAR}
        
        
            and state = #{state,jdbcType=VARCHAR}
        
        
            and file_type = #{fileType,jdbcType=VARCHAR}
        
        
            and file_theme = #{fileTheme,jdbcType=VARCHAR}
        
        
            and bz = #{bz,jdbcType=VARCHAR}
        
        
            and img_type = #{imgType,jdbcType=VARCHAR}
        
        
            and img_desc = #{imgDesc,jdbcType=VARCHAR}
        
        
            and img_quality = #{imgQuality,jdbcType=VARCHAR}
        
        
            and bak1 = #{bak1,jdbcType=VARCHAR}
        
        
            and bak2 = #{bak2,jdbcType=VARCHAR}
        
        
            and bak3 = #{bak3,jdbcType=VARCHAR}
        

        
            order by ${iSortCol} ${sSortDir_0}
