SELECT count(*) from (
SELECT "G0GSJC", round( sum("K0BNYSJHJE")/10000,2) AS "K0BNYSJHJE", round( sum("K0BNGLFPHJECB")/10000,2) AS "K0BNGLFPHJECB", round( sum("K0BNYSJHJE")/10000,2) - round( sum("K0BNGLFPHJECB")/10000,2) AS K0BNYSJY, round( sum("K0BNGLFPHJECB")/NULLIF(sum("K0BNYSJHJE"),0),4) as K0BNZJSYBL FROM ( SELECT "G0GSJC", sum("K0BNYSJHJE") AS "K0BNYSJHJE", sum("K0BNGLFPHJECB") AS "K0BNGLFPHJECB" FROM "_SYS_BIC"."SINOPEC.ZBYY.KJBYY.001_KYGLFXYY/H1AM_KY_SY_1001_01"('PLACEHOLDER' = ('$$PR_CALMONTH$$', '${month}'), 'PLACEHOLDER' = ('$$PR_GSDM$$', '${companyCode}')) group by "G0GSJC" UNION ALL SELECT "G0GSJC", sum("K0BNYSJHJE") AS "K0BNYSJHJE", sum("K0BNGLFPHJECB") AS "K0BNGLFPHJECB" FROM "_SYS_BIC"."SINOPEC.ZBYY.KJBYY.001_KYGLFXYY/H1AM_KY_SY_1001_04"('PLACEHOLDER' = ('$$PR_CALMONTH$$', '${month}'), 'PLACEHOLDER' = ('$$PR_GSDM$$', '${companyCode}')) group by "G0GSJC" ) group by "G0GSJC"
)
