mapper.select:
SELECT A."G0GSSP", IFNULL(sum(B."K0BNXMXKSL"),0) AS "K0BNXMXKSL", IFNULL(sum(B."K0BNXMXJSL"),0) AS "K0BNXMXJSL" FROM ( select distinct "G0GSSP", CASE WHEN "G0GSSP" LIKE '直属研究院' THEN 1 WHEN "G0GSSP" LIKE '分子公司' THEN 2 WHEN "G0GSSP" LIKE '股份付集团' THEN 3 WHEN "G0GSSP" LIKE '外部单位' THEN 4 WHEN "G0GSSP" LIKE '盈科' THEN 5 WHEN "G0GSSP" LIKE '休斯顿研发中心' THEN 6 WHEN "G0GSSP" LIKE '中东研发中心' THEN 7 WHEN "G0GSSP" LIKE '集团公司合计' THEN 8 WHEN "G0GSSP" LIKE '资产公司合计' THEN 9 END AS "G0GSOR" from "H"."SINOPEC.ZBYY.KJBYY.001_KYGLFXYY::H1AM_KY_ZH_0000_01.H1AM_KY_ZH_1000_03" where "G0MXMC" = 'H1AM_KY_ZH_1000_03' ) AS A LEFT JOIN ( SELECT "G0GSSP", sum("K0BNXMXKSL") AS "K0BNXMXKSL", sum("K0BNXMXJSL") AS "K0BNXMXJSL"
FROM "_SYS_BIC"."SINOPEC.ZBYY.KJBYY.001_KYGLFXYY/H1AM_KY_SY_1001_09"('PLACEHOLDER' = ('$$PR_CALMONTH$$', '${month}'), 'PLACEHOLDER' = ('$$PR_GSDM$$', '${companyCode},101I,1018,4270,101H,101F,101A,4370,8280')) WHERE "G0XMXZ" LIKE '%新开%' or "G0XMXZ" LIKE '%续建%' GROUP BY "G0GSSP"
) AS B ON A."G0GSSP" = B."G0GSSP" GROUP BY A."G0GSSP",A."G0GSOR" ORDER BY "G0GSOR"
