mapper.select:
select

    distinct


from zjk_achievement




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
            data_id,
            expert_id,
            user_id,
            achievement_name,
            finish_people,
            industry_id,
            industry_name,
            achievement_keys,
            publish_date,
            achievement_desc,
            achievement_level,
            achievement_type,
            owner_contract_name,
            owner_contract_id,
            owner_project_id,
            owner_project_name,
            compan_id,
            company,
            bak1,
            bak2,
            bak3,
            bak4,
            bak5,
            bak6,
            sys_flag,
            data_order,
            del_flag,
            create_date,
            create_user,
            create_user_disp,
            modify_date,
            modify_user,
            modify_user_disp
from zjk_achievement
where 1=1

                    and data_id = #{dataId,jdbcType=VARCHAR}


                    and expert_id = #{expertId,jdbcType=VARCHAR}


                    and user_id = #{userId,jdbcType=VARCHAR}


                    and achievement_name = #{achievementName,jdbcType=VARCHAR}


                    and finish_people = #{finishPeople,jdbcType=VARCHAR}


                    and industry_id = #{industryId,jdbcType=VARCHAR}


                    and industry_name = #{industryName,jdbcType=VARCHAR}


                    and achievement_keys = #{achievementKeys,jdbcType=VARCHAR}


                    and publish_date = #{publishDate,jdbcType=VARCHAR}


                    and achievement_desc = #{achievementDesc,jdbcType=VARCHAR}


                    and achievement_level = #{achievementLevel,jdbcType=VARCHAR}


                    and achievement_type = #{achievementType,jdbcType=VARCHAR}


                    and owner_contract_name = #{ownerContractName,jdbcType=VARCHAR}


                    and owner_contract_id = #{ownerContractId,jdbcType=VARCHAR}


                    and owner_project_id = #{ownerProjectId,jdbcType=VARCHAR}


                    and owner_project_name = #{ownerProjectName,jdbcType=VARCHAR}


                    and compan_id = #{companId,jdbcType=VARCHAR}


                    and company = #{company,jdbcType=VARCHAR}


                    and bak1 = #{bak1,jdbcType=VARCHAR}


                    and bak2 = #{bak2,jdbcType=VARCHAR}


                    and bak3 = #{bak3,jdbcType=VARCHAR}


                    and bak4 = #{bak4,jdbcType=VARCHAR}


                    and bak5 = #{bak5,jdbcType=VARCHAR}


                    and bak6 = #{bak6,jdbcType=VARCHAR}


                    and sys_flag = #{sysFlag,jdbcType=VARCHAR}


                    and data_order = #{dataOrder,jdbcType=VARCHAR}


                    and del_flag = #{delFlag,jdbcType=VARCHAR}


                    and create_date = #{createDate,jdbcType=VARCHAR}


                    and create_user = #{createUser,jdbcType=VARCHAR}


                    and create_user_disp = #{createUserDisp,jdbcType=VARCHAR}


                    and modify_date = #{modifyDate,jdbcType=VARCHAR}


                    and modify_user = #{modifyUser,jdbcType=VARCHAR}


                    and modify_user_disp = #{modifyUserDisp,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
