mapper.select:
select

    distinct


from zjk_base_info




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
id,
name,
email,
company_name,
company,
user_desc,
zhicheng,
age_between,
age,
csrq,
hyly_name,
hyly,
dz,
byyx,
sheng,
shi,
xian,
je_count,
zl_count,
xm_count,
jg_count,
cg_count,
jwd,
DATA_CODE,
SYS_FLAG,
DATA_ORDER,
STATUS,
DEL_FLAG,
CREATE_DATE,
CREATE_USER,
CREATE_USER_NAME,
UPDATE_DATE,
UPDATE_USER,
UPDATE_USER_NAME,
bak1,
bak2,
bak3,
bak4,
bak5,
bak6,
user_id
from zjk_base_info
where 1=1

    and id = #{id,jdbcType=VARCHAR}


    and name = #{name,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}


    and company_name = #{companyName,jdbcType=VARCHAR}


    and company = #{company,jdbcType=VARCHAR}


    and user_desc = #{userDesc,jdbcType=VARCHAR}


    and zhicheng = #{zhicheng,jdbcType=VARCHAR}


    and age_between = #{ageBetween,jdbcType=VARCHAR}


    and age = #{age,jdbcType=VARCHAR}


    and csrq = #{csrq,jdbcType=VARCHAR}


    and hyly_name = #{hylyName,jdbcType=VARCHAR}


    and hyly = #{hyly,jdbcType=VARCHAR}


    and dz = #{dz,jdbcType=VARCHAR}


    and byyx = #{byyx,jdbcType=VARCHAR}


    and sheng = #{sheng,jdbcType=VARCHAR}


    and shi = #{shi,jdbcType=VARCHAR}


    and xian = #{xian,jdbcType=VARCHAR}


    and je_count = #{jeCount,jdbcType=VARCHAR}


    and zl_count = #{zlCount,jdbcType=VARCHAR}


    and xm_count = #{xmCount,jdbcType=VARCHAR}


    and jg_count = #{jgCount,jdbcType=VARCHAR}


    and cg_count = #{cgCount,jdbcType=VARCHAR}


    and jwd = #{jwd,jdbcType=VARCHAR}


    and DATA_CODE = #{dataCode,jdbcType=VARCHAR}


    and SYS_FLAG = #{sysFlag,jdbcType=VARCHAR}


    and DATA_ORDER = #{dataOrder,jdbcType=VARCHAR}


    and STATUS = #{status,jdbcType=VARCHAR}


    and DEL_FLAG = #{delFlag,jdbcType=VARCHAR}


    and CREATE_DATE = #{createDate,jdbcType=VARCHAR}


    and CREATE_USER = #{createUser,jdbcType=VARCHAR}


    and CREATE_USER_NAME = #{createUserName,jdbcType=VARCHAR}


    and UPDATE_DATE = #{updateDate,jdbcType=VARCHAR}


    and UPDATE_USER = #{updateUser,jdbcType=VARCHAR}


    and UPDATE_USER_NAME = #{updateUserName,jdbcType=VARCHAR}


    and bak1 = #{bak1,jdbcType=VARCHAR}


    and bak2 = #{bak2,jdbcType=VARCHAR}


    and bak3 = #{bak3,jdbcType=VARCHAR}


    and bak4 = #{bak4,jdbcType=VARCHAR}


    and bak5 = #{bak5,jdbcType=VARCHAR}


    and bak6 = #{bak6,jdbcType=VARCHAR}


    and user_id = #{userId,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
