mapper.select:
select

    distinct


from zjk_expert




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
data_id,
expert_name,
email,
company_name,
company,
user_desc,
expert_professinal,
age_between,
age,
birth_date,
industry_name,
industry_id,
address,
college,
province,
city,
county,
amount_count,
patent_count,
project_count,
company_count,
achievement_count,
bak1,
bak2,
bak3,
bak4,
bak5,
bak6,
user_id,
mobile,
expert_professional_field,
expert_professional_field_name,
source,
source_id,
sex,
card_id,
card_type,
education,
header_img,
join_project_name,
join_project_id,
expert_nationality,
sys_flag,
data_order,
del_flag,
create_date,
create_user,
create_user_disp,
modify_date,
modify_user,
modify_user_disp,
form_code,
data_index,
status,
audit_status,
company_code,
update_user,
update_date,
project_id,
project_name,
year,
political_face,
nation,
unit_belongs,
professional_and_time,
administrative_duties,
technical_positiion,
awards_title,
brief_desc,
place_origin
from zjk_expert
where 1=1

    and data_id = #{dataId,jdbcType=VARCHAR}


    and expert_name = #{expertName,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}


    and company_name = #{companyName,jdbcType=VARCHAR}


    and company = #{company,jdbcType=VARCHAR}


    and user_desc = #{userDesc,jdbcType=VARCHAR}


    and expert_professinal = #{expertProfessinal,jdbcType=VARCHAR}


    and age_between = #{ageBetween,jdbcType=VARCHAR}


    and age = #{age,jdbcType=VARCHAR}


    and birth_date = #{birthDate,jdbcType=VARCHAR}


    and industry_name = #{industryName,jdbcType=VARCHAR}


    and industry_id = #{industryId,jdbcType=VARCHAR}


    and address = #{address,jdbcType=VARCHAR}


    and college = #{college,jdbcType=VARCHAR}


    and province = #{province,jdbcType=VARCHAR}


    and city = #{city,jdbcType=VARCHAR}


    and county = #{county,jdbcType=VARCHAR}


    and amount_count = #{amountCount,jdbcType=VARCHAR}


    and patent_count = #{patentCount,jdbcType=VARCHAR}


    and project_count = #{projectCount,jdbcType=VARCHAR}


    and company_count = #{companyCount,jdbcType=VARCHAR}


    and achievement_count = #{achievementCount,jdbcType=VARCHAR}


    and bak1 = #{bak1,jdbcType=VARCHAR}


    and bak2 = #{bak2,jdbcType=VARCHAR}


    and bak3 = #{bak3,jdbcType=VARCHAR}


    and bak4 = #{bak4,jdbcType=VARCHAR}


    and bak5 = #{bak5,jdbcType=VARCHAR}


    and bak6 = #{bak6,jdbcType=VARCHAR}


    and user_id = #{userId,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and expert_professional_field = #{expertProfessionalField,jdbcType=VARCHAR}


    and expert_professional_field_name = #{expertProfessionalFieldName,jdbcType=VARCHAR}


    and source = #{source,jdbcType=VARCHAR}


    and source_id = #{sourceId,jdbcType=VARCHAR}


    and sex = #{sex,jdbcType=VARCHAR}


    and card_id = #{cardId,jdbcType=VARCHAR}


    and card_type = #{cardType,jdbcType=VARCHAR}


    and education = #{education,jdbcType=VARCHAR}


    and header_img = #{headerImg,jdbcType=VARCHAR}


    and join_project_name = #{joinProjectName,jdbcType=VARCHAR}


    and join_project_id = #{joinProjectId,jdbcType=VARCHAR}


    and expert_nationality = #{expertNationality,jdbcType=VARCHAR}


    and sys_flag = #{sysFlag,jdbcType=VARCHAR}


    and data_order = #{dataOrder,jdbcType=VARCHAR}


    and del_flag = #{delFlag,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=VARCHAR}


    and create_user = #{createUser,jdbcType=VARCHAR}


    and create_user_disp = #{createUserDisp,jdbcType=VARCHAR}


    and modify_date = #{modifyDate,jdbcType=VARCHAR}


    and modify_user = #{modifyUser,jdbcType=VARCHAR}


    and modify_user_disp = #{modifyUserDisp,jdbcType=VARCHAR}


    and form_code = #{formCode,jdbcType=VARCHAR}


    and data_index = #{dataIndex,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and audit_status = #{auditStatus,jdbcType=VARCHAR}


    and company_code = #{companyCode,jdbcType=VARCHAR}


    and update_user = #{updateUser,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=VARCHAR}


    and project_id = #{projectId,jdbcType=VARCHAR}


    and project_name = #{projectName,jdbcType=VARCHAR}


    and year = #{year,jdbcType=VARCHAR}


    and political_face = #{politicalFace,jdbcType=VARCHAR}


    and nation = #{nation,jdbcType=VARCHAR}


    and unit_belongs = #{unitBelongs,jdbcType=VARCHAR}


    and professional_and_time = #{professionalAndTime,jdbcType=VARCHAR}


    and administrative_duties = #{administrativeDuties,jdbcType=VARCHAR}


    and technical_positiion = #{technicalPositiion,jdbcType=VARCHAR}


    and awards_title = #{awardsTitle,jdbcType=VARCHAR}


    and brief_desc = #{briefDesc,jdbcType=VARCHAR}


    and place_origin = #{placeOrigin,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
