mapper.select:
select

    distinct


from zjk_patent




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
            data_id,
            user_id,
            expert_id,
            patent_name,
            patent_code,
            patent_desc,
            patent_keys,
            patente_people_name,
            patente_people_id,
            invent_people_id,
            invent_people_name,
            apply_people,
            public_date,
            apply_date,
            bak1,
            bak2,
            bak3,
            bak4,
            bak5,
            bak6,
            modify_user_disp,
            owner_tech_type,
            patente_background,
            owner_project_name,
            expert_nationality,
            owner_project_id,
            company,
            compan_id,
            sys_flag,
            data_order,
            del_flag,
            create_date,
            create_user,
            create_user_disp,
            modify_date,
            modify_user
from zjk_patent
where 1=1

                    and data_id = #{dataId,jdbcType=VARCHAR}


                    and user_id = #{userId,jdbcType=VARCHAR}


                    and expert_id = #{expertId,jdbcType=VARCHAR}


                    and patent_name = #{patentName,jdbcType=VARCHAR}


                    and patent_code = #{patentCode,jdbcType=VARCHAR}


                    and patent_desc = #{patentDesc,jdbcType=VARCHAR}


                    and patent_keys = #{patentKeys,jdbcType=VARCHAR}


                    and patente_people_name = #{patentePeopleName,jdbcType=VARCHAR}


                    and patente_people_id = #{patentePeopleId,jdbcType=VARCHAR}


                    and invent_people_id = #{inventPeopleId,jdbcType=VARCHAR}


                    and invent_people_name = #{inventPeopleName,jdbcType=VARCHAR}


                    and apply_people = #{applyPeople,jdbcType=VARCHAR}


                    and public_date = #{publicDate,jdbcType=VARCHAR}


                    and apply_date = #{applyDate,jdbcType=VARCHAR}


                    and bak1 = #{bak1,jdbcType=VARCHAR}


                    and bak2 = #{bak2,jdbcType=VARCHAR}


                    and bak3 = #{bak3,jdbcType=VARCHAR}


                    and bak4 = #{bak4,jdbcType=VARCHAR}


                    and bak5 = #{bak5,jdbcType=VARCHAR}


                    and bak6 = #{bak6,jdbcType=VARCHAR}


                    and modify_user_disp = #{modifyUserDisp,jdbcType=VARCHAR}


                    and owner_tech_type = #{ownerTechType,jdbcType=VARCHAR}


                    and patente_background = #{patenteBackground,jdbcType=VARCHAR}


                    and owner_project_name = #{ownerProjectName,jdbcType=VARCHAR}


                    and expert_nationality = #{expertNationality,jdbcType=VARCHAR}


                    and owner_project_id = #{ownerProjectId,jdbcType=VARCHAR}


                    and company = #{company,jdbcType=VARCHAR}


                    and compan_id = #{companId,jdbcType=VARCHAR}


                    and sys_flag = #{sysFlag,jdbcType=VARCHAR}


                    and data_order = #{dataOrder,jdbcType=VARCHAR}


                    and del_flag = #{delFlag,jdbcType=VARCHAR}


                    and create_date = #{createDate,jdbcType=VARCHAR}


                    and create_user = #{createUser,jdbcType=VARCHAR}


                    and create_user_disp = #{createUserDisp,jdbcType=VARCHAR}


                    and modify_date = #{modifyDate,jdbcType=VARCHAR}


                    and modify_user = #{modifyUser,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
