mapper.select:
select

    distinct


from zjk_pic




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
            id,
            sql_sql,
            echar_type,
            x,
            y,
            is_dz,
            title,
            sub_title,
            call_back_class,
            call_back_js,
            DEL_FLAG,
            CREATE_DATE,
            CREATE_USER,
            CREATE_USER_NAME,
            UPDATE_DATE,
            UPDATE_USER,
            UPDATE_USER_NAME,
            bak1,
            bak2,
            bak3,
            bak4,
            bak5,
            bak6
from zjk_pic
where 1=1

                    and id = #{id,jdbcType=VARCHAR}


                    and sql_sql = #{sqlSql,jdbcType=VARCHAR}


                    and echar_type = #{echarType,jdbcType=VARCHAR}


                    and x = #{x,jdbcType=VARCHAR}


                    and y = #{y,jdbcType=VARCHAR}


                    and is_dz = #{isDz,jdbcType=VARCHAR}


                    and title = #{title,jdbcType=VARCHAR}


                    and sub_title = #{subTitle,jdbcType=VARCHAR}


                    and call_back_class = #{callBackClass,jdbcType=VARCHAR}


                    and call_back_js = #{callBackJs,jdbcType=VARCHAR}


                    and DEL_FLAG = #{delFlag,jdbcType=VARCHAR}


                    and CREATE_DATE = #{createDate,jdbcType=VARCHAR}


                    and CREATE_USER = #{createUser,jdbcType=VARCHAR}


                    and CREATE_USER_NAME = #{createUserName,jdbcType=VARCHAR}


                    and UPDATE_DATE = #{updateDate,jdbcType=VARCHAR}


                    and UPDATE_USER = #{updateUser,jdbcType=VARCHAR}


                    and UPDATE_USER_NAME = #{updateUserName,jdbcType=VARCHAR}


                    and bak1 = #{bak1,jdbcType=VARCHAR}


                    and bak2 = #{bak2,jdbcType=VARCHAR}


                    and bak3 = #{bak3,jdbcType=VARCHAR}


                    and bak4 = #{bak4,jdbcType=VARCHAR}


                    and bak5 = #{bak5,jdbcType=VARCHAR}


                    and bak6 = #{bak6,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
