mapper.select:
select

    distinct


from laboratory_base




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
data_id,
org_name,
dependent_unit,
org_name_desc,
laboratory_director,
director_desc,
plan_desc,
achievement_desc,
achievement_transformation,
sys_flag,
data_order,
del_flag,
create_date,
create_user,
create_user_disp,
modify_date,
modify_user,
modify_user_disp,
status,
audit_status,
update_user,
update_date,
bak1,
bak2,
bak3,
bak4
from laboratory_base
where 1=1

    and data_id = #{dataId,jdbcType=VARCHAR}


    and org_name = #{orgName,jdbcType=VARCHAR}


    and dependent_unit = #{dependentUnit,jdbcType=VARCHAR}


    and org_name_desc = #{orgNameDesc,jdbcType=VARCHAR}


    and laboratory_director = #{laboratoryDirector,jdbcType=VARCHAR}


    and director_desc = #{directorDesc,jdbcType=VARCHAR}


    and plan_desc = #{planDesc,jdbcType=VARCHAR}


    and achievement_desc = #{achievementDesc,jdbcType=VARCHAR}


    and achievement_transformation = #{achievementTransformation,jdbcType=VARCHAR}


    and sys_flag = #{sysFlag,jdbcType=VARCHAR}


    and data_order = #{dataOrder,jdbcType=VARCHAR}


    and del_flag = #{delFlag,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=VARCHAR}


    and create_user = #{createUser,jdbcType=VARCHAR}


    and create_user_disp = #{createUserDisp,jdbcType=VARCHAR}


    and modify_date = #{modifyDate,jdbcType=VARCHAR}


    and modify_user = #{modifyUser,jdbcType=VARCHAR}


    and modify_user_disp = #{modifyUserDisp,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and audit_status = #{auditStatus,jdbcType=VARCHAR}


    and update_user = #{updateUser,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=VARCHAR}


    and bak1 = #{bak1,jdbcType=VARCHAR}


    and bak2 = #{bak2,jdbcType=VARCHAR}


    and bak3 = #{bak3,jdbcType=VARCHAR}


    and bak4 = #{bak4,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
