mapper.select:
select

    distinct


from laboratory_basic_project




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
data_id,
base_id,
project_sources,
research_direction,
project_name,
project_people,
tech_officer,
project_year,
research_cycle,
project_funds,
project_completion,
project_unit,
remarks,
sys_flag,
data_order,
del_flag,
create_date,
create_user,
create_user_disp,
modify_date,
modify_user,
modify_user_disp,
status,
audit_status,
update_user,
update_date,
bak1,
bak2,
bak3,
bak4
from laboratory_basic_project
where 1=1

    and data_id = #{dataId,jdbcType=VARCHAR}


    and base_id = #{baseId,jdbcType=VARCHAR}


    and project_sources = #{projectSources,jdbcType=VARCHAR}


    and research_direction = #{researchDirection,jdbcType=VARCHAR}


    and project_name = #{projectName,jdbcType=VARCHAR}


    and project_people = #{projectPeople,jdbcType=VARCHAR}


    and tech_officer = #{techOfficer,jdbcType=VARCHAR}


    and project_year = #{projectYear,jdbcType=VARCHAR}


    and research_cycle = #{researchCycle,jdbcType=VARCHAR}


    and project_funds = #{projectFunds,jdbcType=VARCHAR}


    and project_completion = #{projectCompletion,jdbcType=VARCHAR}


    and project_unit = #{projectUnit,jdbcType=VARCHAR}


    and remarks = #{remarks,jdbcType=VARCHAR}


    and sys_flag = #{sysFlag,jdbcType=VARCHAR}


    and data_order = #{dataOrder,jdbcType=VARCHAR}


    and del_flag = #{delFlag,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=VARCHAR}


    and create_user = #{createUser,jdbcType=VARCHAR}


    and create_user_disp = #{createUserDisp,jdbcType=VARCHAR}


    and modify_date = #{modifyDate,jdbcType=VARCHAR}


    and modify_user = #{modifyUser,jdbcType=VARCHAR}


    and modify_user_disp = #{modifyUserDisp,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and audit_status = #{auditStatus,jdbcType=VARCHAR}


    and update_user = #{updateUser,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=VARCHAR}


    and bak1 = #{bak1,jdbcType=VARCHAR}


    and bak2 = #{bak2,jdbcType=VARCHAR}


    and bak3 = #{bak3,jdbcType=VARCHAR}


    and bak4 = #{bak4,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
