mapper.select:
select * from sre_project_task t     

 ${orderBySql}
 
   ORDER BY  create_date desc
 

 limit #{start},#{pageSize}

	     select * from sre_project_task t      where t.audit_status='2'  and t.contract_num !='' and close_status='0'
	      AND t.topic_name like CONCAT('%',#{topicName},'%')
	      AND t.setup_year = #{setupYear}
   		            AND t.unit_path_ids = #{unitPathIds}
	          AND t.parent_unit_path_ids = #{parentUnitPathIds}

select * from sre_project_task t      where t.audit_status='2'  and t.contract_num !='' and close_status='1'
 AND t.topic_name like CONCAT('%',#{topicName},'%')
 AND t.setup_year = #{setupYear}
           AND t.unit_path_ids = #{firstApplyUser}
     AND t.parent_unit_path_ids = #{parentUnitPathIds}
