mapper.select:
select * from sre_purchase t 

  ${orderBySql}
  
    ORDER BY  create_date desc
  

 limit #{start},#{pageSize}

select * from sre_purchase t where t.state >=${state}
               AND t.proposer_name like CONCAT('%',#{proposerName},'%')
               AND t.purchase_name like CONCAT('%',#{purchaseName},'%')
     AND t.create_date = #{createDate}
       AND t.depart_code = #{departCode}
      AND  ${sqlStr}
 limit #{start},#{pageSize}
