mapper.select:
select * from sre_equipment_ledger      

 ${orderBySql}

   ORDER BY  G0GSDM   desc

	 SELECT t.*,t2.`equipment_name` FROM sre_equipment_ledger t  LEFT JOIN sre_equipment_detail t2 ON t.G0ANLN1=t2.asset_number AND t.G0GSDM=t2.supplier
      where LEFT(t.G0ANLN1,2) IN ('02','03')
               AND t.G0GSDM =  #{g0gsdm}
	   	     AND t.G0ANLN1 = #{g0anln1}
	   	       AND t.G0CALD =  #{g0cald}
	   	     AND t.G0TXT50 = #{g0txt50}
	   	      AND t.G0GSJC like CONCAT('%',#{g0gsjc},'%')
