mapper.select:
 select id,name,parent_id as parentId,url,weight,type ,code,remark,status,icon
 from sys_function where(1=1)
 
 and name like '%${name}%'
 
 
 and parent_id = #{parentId}

select id,name,parent_id as pId,url,url as treeUrl,weight,type ,code,remark,status,icon from sys_function where level_code <=3
and id != #{id}
and status = '1'

	select count(id) from sys_function where 
	parent_id = #{parentId}  and name = #{name}
	
 	and id != #{id}

mapper.update:
update sys_function
set isparent = #{isParent}
where id = #{parentId}

update sys_function
set is_leaf = #{isLeaf}
where id = #{parentId}
