mapper.select:
select

    distinct


from zjk_extract_config




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
data_id,
sys_flag,
data_order,
del_flag,
create_date,
create_user,
create_user_disp,
modify_date,
modify_user,
modify_user_disp,
project_stage_id,
project_stage_name,
expert_professional,
expert_count,
expert_area,
interval_time,
extract_weight_in,
extract_weight_out,
company_avoid,
bak1,
bak2,
bak3,
bak4,
bak5,
bak6
from zjk_extract_config
where 1=1

    and data_id = #{dataId,jdbcType=VARCHAR}


    and sys_flag = #{sysFlag,jdbcType=VARCHAR}


    and data_order = #{dataOrder,jdbcType=VARCHAR}


    and del_flag = #{delFlag,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=VARCHAR}


    and create_user = #{createUser,jdbcType=VARCHAR}


    and create_user_disp = #{createUserDisp,jdbcType=VARCHAR}


    and modify_date = #{modifyDate,jdbcType=VARCHAR}


    and modify_user = #{modifyUser,jdbcType=VARCHAR}


    and modify_user_disp = #{modifyUserDisp,jdbcType=VARCHAR}


    and project_stage_id = #{projectStageId,jdbcType=VARCHAR}


    and project_stage_name = #{projectStageName,jdbcType=VARCHAR}


    and expert_professional = #{expertProfessional,jdbcType=VARCHAR}


    and expert_count = #{expertCount,jdbcType=VARCHAR}


    and expert_area = #{expertArea,jdbcType=VARCHAR}


    and interval_time = #{intervalTime,jdbcType=VARCHAR}


    and extract_weight_in = #{extractWeightIn,jdbcType=VARCHAR}


    and extract_weight_out = #{extractWeightOut,jdbcType=VARCHAR}


    and company_avoid = #{companyAvoid,jdbcType=VARCHAR}


    and bak1 = #{bak1,jdbcType=VARCHAR}


    and bak2 = #{bak2,jdbcType=VARCHAR}


    and bak3 = #{bak3,jdbcType=VARCHAR}


    and bak4 = #{bak4,jdbcType=VARCHAR}


    and bak5 = #{bak5,jdbcType=VARCHAR}


    and bak6 = #{bak6,jdbcType=VARCHAR}



    order by ${iSortCol} ${sSortDir_0}
