mapper.select:
select

    distinct


from sys_file

    


    order by ${orderByClause}


    
        limit ${offset}, ${limit}
    
    
        limit ${limit}

        select
        id,
        create_date_time,
        deleted,
        update_date_time,
        version,
        create_user_id,
        file_name,
        file_path,
        file_size,
        form_id,
        saved_name,
        file_md,
        file_suffix,
        start_time,
        end_time,
        file_publish,
        file_pwd,
        bak1,
        bak2,
        bak3,
        bak4,
        file_kind,
        es_id,
        is_del,
        exp_date,
        dataId,
        flag,
        bak5,
        bak6,
        bak7,
        bak8,
        part_img_path,
        bak9,
        bak10
        from sys_file
        where 1=1
        
            and id = #{id,jdbcType=VARCHAR}
        
        
            and create_date_time = #{createDateTime,jdbcType=VARCHAR}
        
        
            and deleted = #{deleted,jdbcType=VARCHAR}
        
        
            and update_date_time = #{updateDateTime,jdbcType=VARCHAR}
        
        
            and version = #{version,jdbcType=VARCHAR}
        
        
            and create_user_id = #{createUserId,jdbcType=VARCHAR}
        
        
            and file_name = #{fileName,jdbcType=VARCHAR}
        
        
            and file_path = #{filePath,jdbcType=VARCHAR}
        
        
            and file_size = #{fileSize,jdbcType=VARCHAR}
        
        
            and form_id = #{formId,jdbcType=VARCHAR}
        
        
            and saved_name = #{savedName,jdbcType=VARCHAR}
        
        
            and file_md = #{fileMd,jdbcType=VARCHAR}
        
        
            and file_suffix = #{fileSuffix,jdbcType=VARCHAR}
        
        
            and start_time = #{startTime,jdbcType=VARCHAR}
        
        
            and end_time = #{endTime,jdbcType=VARCHAR}
        
        
            and file_publish = #{filePublish,jdbcType=VARCHAR}
        
        
            and file_pwd = #{filePwd,jdbcType=VARCHAR}
        
        
            and bak1 = #{bak1,jdbcType=VARCHAR}
        
        
            and bak2 = #{bak2,jdbcType=VARCHAR}
        
        
            and bak3 = #{bak3,jdbcType=VARCHAR}
        
        
            and bak4 = #{bak4,jdbcType=VARCHAR}
        
        
            and file_kind = #{fileKind,jdbcType=VARCHAR}
        
        
            and es_id = #{esId,jdbcType=VARCHAR}
        
        
            and is_del = #{isDel,jdbcType=VARCHAR}
        
        
            and exp_date = #{expDate,jdbcType=VARCHAR}
        
        
            and dataId = #{dataid,jdbcType=VARCHAR}
        
        
            and flag = #{flag,jdbcType=VARCHAR}
        
        
            and bak5 = #{bak5,jdbcType=VARCHAR}
        
        
            and bak6 = #{bak6,jdbcType=VARCHAR}
        
        
            and bak7 = #{bak7,jdbcType=VARCHAR}
        
        
            and bak8 = #{bak8,jdbcType=VARCHAR}
        
        
            and part_img_path = #{partImgPath,jdbcType=VARCHAR}
        
        
            and bak9 = #{bak9,jdbcType=VARCHAR}
        
        
            and bak10 = #{bak10,jdbcType=VARCHAR}
        

        
            order by ${iSortCol} ${sSortDir_0}
