mapper.select:
select

    distinct


from sys_file_share

    


    order by ${orderByClause}


    
        limit ${offset}, ${limit}
    
    
        limit ${limit}

        select
        id,
        file_id,
        user_id,
        stype,
        area,
        create_person_id,
        create_date,
        ramarks,
        status,
        audit_status,
        update_person_id,
        update_date,
        update_person_name,
        create_person_name,
        bak1,
        bak2,
        bak3,
        bak4
        from sys_file_share
        where 1=1
        
            and id = #{id,jdbcType=VARCHAR}
        
        
            and file_id = #{fileId,jdbcType=VARCHAR}
        
        
            and user_id = #{userId,jdbcType=VARCHAR}
        
        
            and stype = #{stype,jdbcType=VARCHAR}
        
        
            and area = #{area,jdbcType=VARCHAR}
        
        
            and create_person_id = #{createPersonId,jdbcType=VARCHAR}
        
        
            and create_date = #{createDate,jdbcType=VARCHAR}
        
        
            and ramarks = #{ramarks,jdbcType=VARCHAR}
        
        
            and status = #{status,jdbcType=VARCHAR}
        
        
            and audit_status = #{auditStatus,jdbcType=VARCHAR}
        
        
            and update_person_id = #{updatePersonId,jdbcType=VARCHAR}
        
        
            and update_date = #{updateDate,jdbcType=VARCHAR}
        
        
            and update_person_name = #{updatePersonName,jdbcType=VARCHAR}
        
        
            and create_person_name = #{createPersonName,jdbcType=VARCHAR}
        
        
            and bak1 = #{bak1,jdbcType=VARCHAR}
        
        
            and bak2 = #{bak2,jdbcType=VARCHAR}
        
        
            and bak3 = #{bak3,jdbcType=VARCHAR}
        
        
            and bak4 = #{bak4,jdbcType=VARCHAR}
        

        
            order by ${iSortCol} ${sSortDir_0}

mapper.delete:
delete from sys_file_share
where 1=1

    and id = #{id,jdbcType=VARCHAR}


    and file_id = #{fileId,jdbcType=VARCHAR}


    and user_id = #{userId,jdbcType=VARCHAR}


    and stype = #{stype,jdbcType=VARCHAR}


    and area = #{area,jdbcType=VARCHAR}


    and create_person_id = #{createPersonId,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=VARCHAR}


    and ramarks = #{ramarks,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and audit_status = #{auditStatus,jdbcType=VARCHAR}


    and update_person_id = #{updatePersonId,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=VARCHAR}


    and update_person_name = #{updatePersonName,jdbcType=VARCHAR}


    and create_person_name = #{createPersonName,jdbcType=VARCHAR}


    and bak1 = #{bak1,jdbcType=VARCHAR}


    and bak2 = #{bak2,jdbcType=VARCHAR}


    and bak3 = #{bak3,jdbcType=VARCHAR}


    and bak4 = #{bak4,jdbcType=VARCHAR}

delete from sys_file_share
where 1=1
   and file_id =  #{fileId,jdbcType=VARCHAR}
