mapper.insert:
insert into out_appraisal (data_id, cgid, nd, cgmc, sqjddw, sqdjh, StartTime, EndTime, cglx, zy, jdh, issczs, sbzt, jdjg, create_date, create_user_id, update_date, update_user_id, status, remarks,hth, xmmc, rwly,define1,define2,define3,define4,define5,define6) values
( #{appraisal.dataId,jdbcType=VARCHAR}, #{appraisal.cgid,jdbcType=VARCHAR}, #{appraisal.nd,jdbcType=VARCHAR}, #{appraisal.cgmc,jdbcType=VARCHAR}, #{appraisal.sqjddw,jdbcType=VARCHAR}, #{appraisal.sqdjh,jdbcType=VARCHAR}, #{appraisal.starttime,jdbcType=VARCHAR}, #{appraisal.endtime,jdbcType=VARCHAR}, #{appraisal.cglx,jdbcType=VARCHAR}, #{appraisal.zy,jdbcType=VARCHAR}, #{appraisal.jdh,jdbcType=VARCHAR}, #{appraisal.issczs,jdbcType=VARCHAR}, #{appraisal.sbzt,jdbcType=VARCHAR}, #{appraisal.jdjg,jdbcType=VARCHAR}, #{appraisal.createDate,jdbcType=TIMESTAMP}, #{appraisal.createUserId,jdbcType=VARCHAR}, #{appraisal.updateDate,jdbcType=TIMESTAMP}, #{appraisal.updateUserId,jdbcType=VARCHAR}, #{appraisal.status,jdbcType=VARCHAR}, #{appraisal.remarks,jdbcType=VARCHAR}, #{appraisal.hth,jdbcType=VARCHAR}, #{appraisal.xmmc,jdbcType=VARCHAR}, #{appraisal.rwly,jdbcType=VARCHAR}, #{appraisal.define1,jdbcType=VARCHAR}, #{appraisal.define2,jdbcType=VARCHAR}, #{appraisal.define3,jdbcType=VARCHAR}, #{appraisal.define4,jdbcType=VARCHAR}, #{appraisal.define5,jdbcType=VARCHAR}, #{appraisal.define6,jdbcType=VARCHAR}
)

mapper.select:
select tableA.* from sys_dictionary left join ( select count(DISTINCT jdh) as mscount, ifnull(jdjg, '其他') as ms from out_appraisal where define3 not like '%外部单位%'
and nd = #{nd,jdbcType=VARCHAR}
group by jdjg ) as tableA ON (sys_dictionary.name = tableA.ms) where parent_code = 'ROOT_FZJCZX_JDJG' order by isdelete

select sum(case when jdjg = '国内先进' then 1 else 0 end) as gnxjcount, sum(case when jdjg = '国内领先' then 1 else 0 end) as gnlxcount, sum(case when jdjg = '国际先进' then 1 else 0 end) as gjxjcount, sum(case when jdjg = '国际领先' then 1 else 0 end) as gjlxcount, sum(case when jdjg = '行业先进' then 1 else 0 end) as hyxjcount, sum(case when jdjg = '行业领先' then 1 else 0 end) as hylxcount, sum(case when jdjg = '其他' then 1 else 0 end) as qtcount, count(jdh) as cglxcount,cglx from ( select jdh,cglx,ifnull(GROUP_CONCAT(DISTINCT jdjg), '其他') jdjg from out_appraisal where define3 not like '%外部单位%'
and nd = #{nd,jdbcType=VARCHAR}
GROUP BY jdh,cglx ) as tableA group by cglx ORDER BY cglx

select tableA.* from sys_dictionary left join ( select sum(case when zy = 'A.石油勘探' then 1 else 0 end) as acount, sum(case when zy = 'B.石油开发' then 1 else 0 end) as bcount, sum(case when zy = 'C.石油工程' then 1 else 0 end) as ccount, sum(case when zy = 'D.石油炼制' then 1 else 0 end) as dcount, sum(case when zy = 'E.化工' then 1 else 0 end) as ecount, sum(case when zy = 'F.化纤' then 1 else 0 end) as fcount, sum(case when zy = 'G.装备与储运' then 1 else 0 end) as gcount, sum(case when zy = 'H.安全与环保' then 1 else 0 end) as hcount, sum(case when zy = 'I.信息技术' then 1 else 0 end) as icount, sum(case when zy = 'J.大型装备国产化' then 1 else 0 end) as jcount, sum(case when zy = 'K.其它' then 1 else 0 end) as kcount, ifnull(jdjg, '其他') as jdjg from ( select jdh,zy,jdjg from out_appraisal where define3 not like '%外部单位%'
and nd = #{nd,jdbcType=VARCHAR}
GROUP BY jdh,zy ) as tableA group by jdjg ) as tableA ON (sys_dictionary.name = tableA.jdjg) where parent_code = 'ROOT_FZJCZX_JDJG' order by isdelete
