mapper.insert:
insert into out_appraisal (data_id, cgid, nd, cgmc, sqjddw, sqdjh, StartTime, EndTime, cglx, zy, jdh, issczs, sbzt, jdjg, create_date, create_user_id, update_date, update_user_id, status, remarks,hth, xmmc, rwly,define1,define2,define3,define4,define5,define6) values
( #{appraisal.dataId,jdbcType=VARCHAR}, #{appraisal.cgid,jdbcType=VARCHAR}, #{appraisal.nd,jdbcType=VARCHAR}, #{appraisal.cgmc,jdbcType=VARCHAR}, #{appraisal.sqjddw,jdbcType=VARCHAR}, #{appraisal.sqdjh,jdbcType=VARCHAR}, #{appraisal.starttime,jdbcType=VARCHAR}, #{appraisal.endtime,jdbcType=VARCHAR}, #{appraisal.cglx,jdbcType=VARCHAR}, #{appraisal.zy,jdbcType=VARCHAR}, #{appraisal.jdh,jdbcType=VARCHAR}, #{appraisal.issczs,jdbcType=VARCHAR}, #{appraisal.sbzt,jdbcType=VARCHAR}, #{appraisal.jdjg,jdbcType=VARCHAR}, #{appraisal.createDate,jdbcType=TIMESTAMP}, #{appraisal.createUserId,jdbcType=VARCHAR}, #{appraisal.updateDate,jdbcType=TIMESTAMP}, #{appraisal.updateUserId,jdbcType=VARCHAR}, #{appraisal.status,jdbcType=VARCHAR}, #{appraisal.remarks,jdbcType=VARCHAR}, #{appraisal.hth,jdbcType=VARCHAR}, #{appraisal.xmmc,jdbcType=VARCHAR}, #{appraisal.rwly,jdbcType=VARCHAR}, #{appraisal.define1,jdbcType=VARCHAR}, #{appraisal.define2,jdbcType=VARCHAR}, #{appraisal.define3,jdbcType=VARCHAR}, #{appraisal.define4,jdbcType=VARCHAR}, #{appraisal.define5,jdbcType=VARCHAR}, #{appraisal.define6,jdbcType=VARCHAR}
)

mapper.select:
select count(*) as mscount,jdjg as ms from out_appraisal where jdjg is not null
and nd = #{nd,jdbcType=VARCHAR}
group by jdjg

select sum(case when jdjg = '国内先进' then 1 else 0 end) as gnxjcount, sum(case when jdjg = '国内领先' then 1 else 0 end) as gnlxcount, sum(case when jdjg = '国际先进' then 1 else 0 end) as gjxjcount, sum(case when jdjg = '国际领先' then 1 else 0 end) as gjlxcount, sum(case when jdjg = '行业先进' then 1 else 0 end) as hyxjcount, sum(case when jdjg = '行业领先' then 1 else 0 end) as hylxcount,count(jdjg) as cglxcount,cglx from out_appraisal where jdjg in ('国内先进','国内领先','国际先进','国际领先','行业先进','行业领先')
and nd = #{nd,jdbcType=VARCHAR}
group by cglx

select sum(case when zy = 'A.石油勘探' then 1 else 0 end) as acount, sum(case when zy = 'B.石油开发' then 1 else 0 end) as bcount, sum(case when zy = 'C.石油工程' then 1 else 0 end) as ccount, sum(case when zy = 'D.石油炼制' then 1 else 0 end) as dcount, sum(case when zy = 'E.化工' then 1 else 0 end) as ecount, sum(case when zy = 'F.化纤' then 1 else 0 end) as fcount, sum(case when zy = 'G.装备与储运' then 1 else 0 end) as gcount, sum(case when zy = 'H.安全与环保' then 1 else 0 end) as hcount, sum(case when zy = 'I.信息技术' then 1 else 0 end) as icount, sum(case when zy = 'J.大型装备国产化' then 1 else 0 end) as jcount, sum(case when zy = 'K.其它' then 1 else 0 end) as kcount,jdjg from out_appraisal
where nd = #{nd,jdbcType=VARCHAR}
group by jdjg

select count(*) mscount, '研发中心' as unitType from out_appraisal WHERE sqjddw like '%中心%'
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '集团单位' as unitType from out_appraisal WHERE sqjddw like '%集团%'
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '直属研究院' as unitType from out_appraisal WHERE sqjddw like '%研究院%'
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '股份分公司' as unitType from out_appraisal WHERE sqjddw like '%分公司%'
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '外部单位' as unitType from out_appraisal WHERE sqjddw like '%大学%'
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '盈科' as unitType from out_appraisal WHERE sqjddw like '%盈科%'
and nd = #{nd,jdbcType=VARCHAR}

select count(*) mscount, '石科院' as unitType from out_appraisal WHERE (sqjddw like '%中国石油化工股份有限公司石油化工科学研究院%')
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '北化院' as unitType from out_appraisal WHERE (sqjddw like '%北京北化院石化设计院有限公司%' or sqjddw like '%中国石油化工股份有限公司北京化工研究院%')
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '大连院' as unitType from out_appraisal WHERE (sqjddw like '%中国石油化工股份有限公司抚顺石油化工研究院%' or sqjddw like '%抚顺环科石油化工技术开发有限公司%')
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '上海院' as unitType from out_appraisal WHERE (sqjddw like '%中国石油化工股份有限公司上海石油化工研究院%')
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '安工院' as unitType from out_appraisal WHERE (sqjddw like '%中国石油化工股份有限公司青岛安全工程研究院%' or sqjddw like '%青岛中化阳光管理体系认证中心有限公司%')
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '勘探院' as unitType from out_appraisal WHERE (sqjddw like '%中国石油化工股份有限公司石油勘探开发研究院%' or sqjddw like '%中国石油化工股份有限公司石油勘探开发研究院无锡石油%' or sqjddw like '%中国石化集团资产经营管理有限公司合肥培训测试中心%' or sqjddw like '%中国石化石油勘探开发研究院有限公司%')
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '物探院' as unitType from out_appraisal WHERE (sqjddw like '%中国石油化工股份有限公司石油物探技术研究院%')
and nd = #{nd,jdbcType=VARCHAR}
union all select count(*) mscount, '工程院' as unitType from out_appraisal WHERE (sqjddw like '%中国石油化工股份有限公司石油工程技术研究院%')
and nd = #{nd,jdbcType=VARCHAR}
