mapper.select:
select
,
from out_project_info where data_id = #{dataId,jdbcType=VARCHAR}

select sum(case when project_property = '' or project_property = '' then 1 else 0 end) as zsl, sum(case when project_property = '' then 1 else 0 end) as zdzxsl, sum(case when project_property = '' then 1 else 0 end) as stlsl from ( select DISTINCT xmmc,project_property from out_project_info where (project_type = '0' or project_type = '1') and nd='${nd}'
and define2 is not null and type_flag like '%%'
) as tableA

select nd,xmmc,fzdw as zydw,`status`,define6,jdap,hth from out_project_info WHERE project_property = '' and define6 = '4'
and out_project_info.nd = #{nd}
and out_project_info.ysnd = #{ysnd}
and out_project_info.xmmc like '%${xmmc}%'
and (
out_project_info.status like '%${status}%'
)
and (
out_project_info.define2 like '%${define2}%'
)
and (
out_project_info.xmlbmc like '%${xmlbmc}%'
)
and (
out_project_info.type_flag like '%${type_flag}%'
)
union all select nd,xmmc,GROUP_CONCAT(fzdw) as zydw,`status`,define6,jdap,hth from out_project_info WHERE project_property = '' and (define6 = '1' or define6 = '2')
and out_project_info.nd = #{nd}
and out_project_info.ysnd = #{ysnd}
and out_project_info.xmmc like '%${xmmc}%'
and (
out_project_info.status like '%${status}%'
)
and (
out_project_info.define2 like '%${define2}%'
)
and (
out_project_info.xmlbmc like '%${xmlbmc}%'
)
and (
out_project_info.type_flag like '%${type_flag}%'
)
GROUP BY xmmc
union all select nd,xmmc,GROUP_CONCAT(fzdw) as zydw,`status`,define6,jdap,hth from out_project_info WHERE project_property = '' and define6 = '3'
and out_project_info.nd = #{nd}
and out_project_info.ysnd = #{ysnd}
and out_project_info.xmmc like '%${xmmc}%'
and (
out_project_info.status like '%${status}%'
)
and (
out_project_info.define2 like '%${define2}%'
)
and (
out_project_info.xmlbmc like '%${xmlbmc}%'
)
and (
out_project_info.type_flag like '%${type_flag}%'
)
GROUP BY xmmc order by hth,define6

select out_project_info.define1,substr(out_project_info.define2 from 4) as define2,substr(out_project_info.type_flag from 2) as type_flag, out_project_info.xmmc,out_project_info.jf,out_project_info.ysje,out_project_info.ysnd, out_project_info.project_property,out_project_info.fzdw,out_project_info.zylb, out_project_info.nd,out_project_info.hth,out_project_info.zycmc from out_project_info where out_project_info.project_type != '1' and out_project_info.project_property not like '%%'
and out_project_info.nd = #{nd}
and out_project_info.ysnd = #{ysnd}
and out_project_info.hth like '%${hth}%'
and out_project_info.xmmc like '%${xmmc}%'
and (
out_project_info.define1 like '%${define1}%'
)
and (
out_project_info.define2 like '%${define2}%'
)
and (
out_project_info.define5 like '%${define5}%'
)
and (
out_project_info.project_property like '%${project_property}%'
)
and out_project_info.project_property not like '%%'
and (
out_project_info.zylb like '%${zylb}%'
)
and (
out_project_info.type_flag like '%${type_flag}%'
)
and (
out_project_info.zycmc like '%${zycmc}%'
)
${keyword}
order by ${orderKey} ${orderType}
order by ${orderKey} desc
