mapper.select:
select

from plan_base where STATUS = '0'

    and (WORK_ORDER_NAME like '%${workOrderName}%' OR WORK_ORDER_CODE like '%${workOrderName}%')


    and WORK_ORDER_TYPE = #{workOrderType}


    and WORK_ORDER_STATUS = #{workOrderStatus}


    and DEL_FLAG = #{delFlag}


    and AUDIT_STS = #{auditSts}

order by CREATE_DATE desc

select count(*) from plan_base where STATUS = '0'

    and (WORK_ORDER_NAME like '%${workOrderName}%' OR WORK_ORDER_CODE like '%${workOrderName}%')


    and WORK_ORDER_TYPE = #{workOrderType}


    and WORK_ORDER_STATUS = #{workOrderStatus}


    and DEL_FLAG = #{delFlag}


    and AUDIT_STS = #{auditSts}

select

from plan_base where STATUS = '0'

    and (WORK_ORDER_NAME like '%${workOrderName}%' OR WORK_ORDER_CODE like '%${workOrderName}%')


    and WORK_ORDER_ALLOT_USER_ID = #{workOrderAllotUserId}


    and WORK_ORDER_TYPE = #{workOrderType}


    and WORK_ORDER_STATUS = #{workOrderStatus}

and WORK_ORDER_STATUS != '0'

    and DEL_FLAG = #{delFlag}


    and AUDIT_STS = #{auditSts}

order by CREATE_DATE desc

select count(*) from plan_base where STATUS = '0'

    and (WORK_ORDER_NAME like '%${workOrderName}%' OR WORK_ORDER_CODE like '%${workOrderName}%')


    and WORK_ORDER_ALLOT_USER_ID = #{workOrderAllotUserId}

and WORK_ORDER_STATUS != '0'

    and WORK_ORDER_TYPE = #{workOrderType}


    and WORK_ORDER_STATUS = #{workOrderStatus}


    and DEL_FLAG = #{delFlag}


    and AUDIT_STS = #{auditSts}
