mapper.select:
select

    distinct


from report_column

    


    order by ${orderByClause}


    
        limit ${offset}, ${limit}
    
    
        limit ${limit}

        select
        id,
        report_config_id,
        field,
        title,
        width,
        minWidth,
        type,
        sort,
        unresize,
        edit,
        event,
        style,
        align,
        colspan,
        rowspan,
        templet,
        toolbar,
        role,
        sf_showlist,
        sf_x,
        sf_y,
        sf_ajax,
        stype,
        ssort,
        sf_show,
        sf_del,
        sf_use,
        bak1,
        bak2,
        bak3,
        bak4,
        bak5,
        bak6,
        create_user,
        create_user_id,
        create_date,
        status,
        update_date,
        update_user,
        remarks,
        audit_status,
        fileId
        from report_column
        where 1=1
        
            and id = #{id,jdbcType=VARCHAR}
        
        
            and report_config_id = #{reportConfigId,jdbcType=VARCHAR}
        
        
            and field = #{field,jdbcType=VARCHAR}
        
        
            and title = #{title,jdbcType=VARCHAR}
        
        
            and width = #{width,jdbcType=VARCHAR}
        
        
            and minWidth = #{minwidth,jdbcType=VARCHAR}
        
        
            and type = #{type,jdbcType=VARCHAR}
        
        
            and sort = #{sort,jdbcType=VARCHAR}
        
        
            and unresize = #{unresize,jdbcType=VARCHAR}
        
        
            and edit = #{edit,jdbcType=VARCHAR}
        
        
            and event = #{event,jdbcType=VARCHAR}
        
        
            and style = #{style,jdbcType=VARCHAR}
        
        
            and align = #{align,jdbcType=VARCHAR}
        
        
            and colspan = #{colspan,jdbcType=VARCHAR}
        
        
            and rowspan = #{rowspan,jdbcType=VARCHAR}
        
        
            and templet = #{templet,jdbcType=VARCHAR}
        
        
            and toolbar = #{toolbar,jdbcType=VARCHAR}
        
        
            and role = #{role,jdbcType=VARCHAR}
        
        
            and sf_showlist = #{sfShowlist,jdbcType=VARCHAR}
        
        
            and sf_x = #{sfX,jdbcType=VARCHAR}
        
        
            and sf_y = #{sfY,jdbcType=VARCHAR}
        
        
            and sf_ajax = #{sfAjax,jdbcType=VARCHAR}
        
        
            and stype = #{stype,jdbcType=VARCHAR}
        
        
            and ssort = #{ssort,jdbcType=VARCHAR}
        
        
            and sf_show = #{sfShow,jdbcType=VARCHAR}
        
        
            and sf_del = #{sfDel,jdbcType=VARCHAR}
        
        
            and sf_use = #{sfUse,jdbcType=VARCHAR}
        
        
            and bak1 = #{bak1,jdbcType=VARCHAR}
        
        
            and bak2 = #{bak2,jdbcType=VARCHAR}
        
        
            and bak3 = #{bak3,jdbcType=VARCHAR}
        
        
            and bak4 = #{bak4,jdbcType=VARCHAR}
        
        
            and bak5 = #{bak5,jdbcType=VARCHAR}
        
        
            and bak6 = #{bak6,jdbcType=VARCHAR}
        
        
            and create_user = #{createUser,jdbcType=VARCHAR}
        
        
            and create_user_id = #{createUserId,jdbcType=VARCHAR}
        
        
            and create_date = #{createDate,jdbcType=VARCHAR}
        
        
            and status = #{status,jdbcType=VARCHAR}
        
        
            and update_date = #{updateDate,jdbcType=VARCHAR}
        
        
            and update_user = #{updateUser,jdbcType=VARCHAR}
        
        
            and remarks = #{remarks,jdbcType=VARCHAR}
        
        
            and audit_status = #{auditStatus,jdbcType=VARCHAR}
        
        
            and fileId = #{fileid,jdbcType=VARCHAR}
        

        
            order by ${iSortCol} ${sSortDir_0}
