mapper.select:
select

    distinct


from report_store

    


    order by ${orderByClause}


    
        limit ${offset}, ${limit}
    
    
        limit ${limit}

        select
        id,
        model_id,
        model_config_id,
        tj,
        zb,
        rq,
        user_name,
        user_id,
        cxtj,
        bak1,
        bak2,
        bak3,
        bak4,
        bak5,
        bak6,
        create_user,
        create_user_id,
        create_date,
        status,
        update_date,
        update_user,
        remarks,
        audit_status,
        json
        from report_store
        where 1=1
        
            and id = #{id,jdbcType=VARCHAR}
        
        
            and model_id = #{modelId,jdbcType=VARCHAR}
        
        
            and model_config_id = #{modelConfigId,jdbcType=VARCHAR}
        
        
            and tj = #{tj,jdbcType=VARCHAR}
        
        
            and zb = #{zb,jdbcType=VARCHAR}
        
        
            and rq = #{rq,jdbcType=VARCHAR}
        
        
            and user_name = #{userName,jdbcType=VARCHAR}
        
        
            and user_id = #{userId,jdbcType=VARCHAR}
        
        
            and cxtj = #{cxtj,jdbcType=VARCHAR}
        
        
            and bak1 = #{bak1,jdbcType=VARCHAR}
        
        
            and bak2 = #{bak2,jdbcType=VARCHAR}
        
        
            and bak3 = #{bak3,jdbcType=VARCHAR}
        
        
            and bak4 = #{bak4,jdbcType=VARCHAR}
        
        
            and bak5 = #{bak5,jdbcType=VARCHAR}
        
        
            and bak6 = #{bak6,jdbcType=VARCHAR}
        
        
            and create_user = #{createUser,jdbcType=VARCHAR}
        
        
            and create_user_id = #{createUserId,jdbcType=VARCHAR}
        
        
            and create_date = #{createDate,jdbcType=VARCHAR}
        
        
            and status = #{status,jdbcType=VARCHAR}
        
        
            and update_date = #{updateDate,jdbcType=VARCHAR}
        
        
            and update_user = #{updateUser,jdbcType=VARCHAR}
        
        
            and remarks = #{remarks,jdbcType=VARCHAR}
        
        
            and audit_status = #{auditStatus,jdbcType=VARCHAR}
        
        
            and json = #{json,jdbcType=VARCHAR}
        

        
            order by ${iSortCol} ${sSortDir_0}
