mapper.insert:
insert into t_department (id, title, `type`, 
  create_time, create_user_id, breif, 
  develop_history, organization, personnel, 
  honor, major, notes
  )
values (#{id,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{createUserId,jdbcType=VARCHAR}, #{breif,jdbcType=LONGVARCHAR}, 
  #{developHistory,jdbcType=LONGVARCHAR}, #{organization,jdbcType=LONGVARCHAR}, #{personnel,jdbcType=LONGVARCHAR}, 
  #{honor,jdbcType=LONGVARCHAR}, #{major,jdbcType=LONGVARCHAR}, #{notes,jdbcType=LONGVARCHAR}
  )

mapper.select:
select * from t_department t     

 ${orderBySql}
 
   ORDER BY  create_time desc
 

 limit #{start},#{pageSize}
